#define LONGRUNNING /* long-running command */

#define CMD_UNIQUEPROC  (1 << 0)
#define CMD_USEDOTSAV   (1 << 1)
#define CMD_LONGRUNNING (1 << 2)

int cmd_help(int argc, const char **argv);
